                    self.posX-=1;
                    self.walkBackAnimation.play();
                    break;
                case ve.keyboard.KEY_RIGHT:
                    self.posX+=1;
                    self.walkFwdAnimation.play();
                    break;
                default:
                    break;
            }
        });
        ve.keyboard.onKeyUp(function(){
            self.stopFrAnimations();
            self.setFrameIndex(0);
        });
    },

