ve.models.Behaviour.extend({

    walkAnimation:null,

    onCreate: function(){
        this.walkFwdAnimation = this.getFrAnimation('walkFwd');
        this.walkBackAnimation = this.getFrAnimation('walkBack');
    },

    onUpdate: function(time) {
        if (ve.keyboard.isKeyPressed(ve.keyboard.KEY_LEFT)) {
            this.posX-=1;
            this.walkBackAnimation.play();
        }
        if (ve.keyboard.isKeyPressed(ve.keyboard.KEY_RIGHT)) {
            this.posX+=1;
            this.walkFwdAnimation.play();
        }
        if (ve.keyboard.isKeyJustUnPressed(ve.keyboard.KEY_RIGHT) || ve.keyboard.isKeyJustUnPressed(ve.keyboard.KEY_LEFT)) {
            this.stopFrAnimations();
        }

    },

    onDestroy: function(){

    }

});
